import Head from 'next/head'
import styles from '../styles/Home.module.css'
import styled from 'styled-components';
import React, { useEffect, useRef, useState } from "react";


const Input = styled.input`
  background-color: transparent;
  caret-color: white;
  caret-shape:underscore;

  border:none;
  color:white;
  font-family: Orbitron, sans-serif;
  font-size: 50px;
  text-shadow: 1px 1px 10px white;
  text-align:center;
  outline:none;
  height:100vh;
  width:100%;
  overflow:hidden;
`
const Title = styled.h1`
  color:white;
  position:absolute;
  top:0;
  font-family: Orbitron, sans-serif;
  text-shadow: 1px 1px 10px white;
  font-size: 100px;
`
const Signature = styled.a`
  font-family: Orbitron, sans-serif;
  color:white;
  text-shadow: 1px 1px 10px white;
  position:absolute;
  bottom:10px;
`
const handleKeyUp = e=>{
  console.log(e.currentTarget.value)
  if (e.key === "Enter" && !e.shiftKey) {
    console.log("vous avez appuyé sur enter")
  }
}
export default function Home() {
  const searchInput = useRef(null);
useEffect(()=>{
  searchInput.current.focus();
  console.log(searchInput);
},[]) 
  return (
    <div className={styles.container}>
      <Head>
        <title>Terminal site</title>
        <link rel="icon" href="/favicon.ico" />
        <link href="https://fonts.googleapis.com/css2?family=Orbitron:wght@400;500;600;700;800;900&display=swap" rel="stylesheet"></link>
      </Head>
      <Title>Terminal Site</Title>
      <Input ref={searchInput} onKeyUp={handleKeyUp} value="■"></Input>
      <Signature href="https://github.com/guienjoris" target="_blank" >Shynryu</Signature>
    </div>
  )
}
